USE bdEscolaDeIdiomas--a) Criar uma consulta que retorne o nome e o preço dos cursos que custem abaixo do valor médioSELECT nomeCurso, valorCurso FROM tbCurso	WHERE valorCurso < (SELECT AVG(tbCurso.valorCurso) FROM tbCurso)--b) Criar uma consulta que retorne o nome e o rg do aluno mais novoSELECT nomeAluno, rgAluno FROM tbAluno	WHERE dataNascAluno = (SELECT MIN(tbAluno.dataNascAluno) FROM tbAluno)--c) Criar uma consulta que retorne o nome do aluno mais velhoSELECT DISTINCT nomeAluno FROM tbAluno	WHERE dataNascAluno = (SELECT MAX(tbAluno.dataNascAluno) FROM tbAluno)--d) Criar uma consulta que retorne o nome e o valor do curso mais caroSELECT DISTINCT nomeCurso, valorCurso FROM tbCurso	WHERE valorCurso = (SELECT MAX(tbCurso.valorCurso) FROM tbCurso)--e) Criar uma consulta que retorne o nome do aluno e o nome do curso, do aluno que fez a última matrículaSELECT DISTINCT nomeAluno, nomeCurso FROM tbAluno	INNER JOIN tbMatricula ON tbAluno.codAluno = tbMatricula.codAluno		INNER JOIN tbTurma ON tbMatricula.codTurma = tbTurma.codTurma			INNER JOIN tbCurso ON tbTurma.codCurso = tbCurso.codCurso				WHERE dataMatricula = (SELECT MIN(tbMatricula.dataMatricula) FROM tbMatricula)--f) Criar uma consulta que retorne o nome do primeiro aluno a ser matriculado na escola de IdiomasSELECT DISTINCT nomeAluno FROM tbAluno	INNER JOIN tbMatricula ON tbAluno.codAluno = tbMatricula.codAluno		INNER JOIN tbTurma ON tbMatricula.codTurma = tbTurma.codTurma			INNER JOIN tbCurso ON tbTurma.codCurso = tbCurso.codCurso				WHERE dataMatricula = (SELECT MAX(tbMatricula.dataMatricula) FROM tbMatricula)--g) Criar uma consulta que retorne o nome, rg e data de nascimento de todos os alunos que estejam matriculados no curso de inglêsSELECT DISTINCT nomeAluno, rgAluno, dataNascAluno FROM tbAluno	INNER JOIN tbMatricula ON tbAluno.codAluno = tbMatricula.codAluno		INNER JOIN tbTurma ON tbMatricula.codTurma = tbTurma.codTurma			INNER JOIN tbCurso ON tbTurma.codCurso = tbCurso.codCurso				WHERE nomeCurso LIKE '%Inglês%'