USE bdEscolaDeIdiomas--a) Criar uma consulta que retorne o nome e o preço dos cursos que custem abaixo do valor médioSELECT nomeCurso, valorCurso FROM tbCurso	WHERE valorCurso < (SELECT AVG(tbCurso.valorCurso) FROM tbCurso)--b) Criar uma consulta que retorne o nome e o rg do aluno mais novoSELECT nomeAluno, rgAluno FROM tbAluno	WHERE dataNascAluno = (SELECT MIN(tbAluno.dataNascAluno) FROM tbAluno)